for idx = 1, 600 do
    local res = native_GetCSWeaponInfo(IWeaponSystem_raw, idx)

    if res ~= nil then
        local weapon = {}

        for i = 1, #struct_keys do
            local key = struct_keys[i]
            local val = res[key]

            local ct_success, ct = pcall(ffi.typeof, val)
            weapon[key] = ct_success and ct == ctype_char and ffi.string(val) or val
        end

        weapon.idx = idx
        weapon.type = idx == 31 and "taser" or weapon_types[res.weapon_type_int]
        weapon.name = weapon.item_name
        weapon.raw = res

        weapon.schema = schema_t[idx]

        weapons[idx] = weapon
        weapons_index[weapon.console_name] = weapon
    end
end

local function weapons_get_by_entity(tbl, entindex)
    if tbl ~= weapons or type(entindex) ~= "number" or entindex < 0 or entindex > 8191 then
        return
    end

    local idx = entity_list.get_entity(entindex):get_prop("m_iItemDefinitionIndex")
    return weapons[idx]
end

setmetatable(weapons, {
    __index = weapons_index,
    __metatable = false,
    __call = weapons_get_by_entity
})

return weapons